mapper.select:
SELECT * FROM board


ORDER BY top_notice DESC, regdate DESC
LIMIT ${startIdx},${numchoose}

mapper.insert:
INSERT INTO board (
	title, regdate, content, filename_org, filename_real, 
	readcount, userno, top_notice, new_icon, secret, category
) values(
	#{title}, 
	'${sendDate}'
	NOW(), 
	#{content},#{filename_org},#{filename_real}, 
	#{readcount}, #{userno}, #{top_notice}, #{new_icon}, #{secret}, #{category}
)

mapper.update:
UPDATE board SET 
title = #{title}
, top_notice = #{top_notice}
, new_icon = #{new_icon}
, secret = #{secret}
, content=#{content}
, category=#{category}

, filename_org=#{filename_org}
, filename_real=#{filename_real}

WHERE no=#{no}
